import { useState } from "react";
import UserContext from "./context/UserContext";
import "./App.css";
import User from "./components/User";
function App() {
  const [user, setUser] = useState("Daniele");
  return (
    <UserContext.Provider value={user}>

    <div className="App">
      <User user={user} />
    </div>
    </UserContext.Provider>
  );
}

export default App;
